mapper.update:
UPDATE weibo
                        SET content = #{content},
                            created = #{created},
                            user_id = #{userId}
                        WHERE id = #{id}

mapper.select:
SELECT id, content, created, user_id userId
                                                                          FROM weibo
                                                                          WHERE id = #{id}

SELECT id, content, created, user_id userId
                                                              FROM weibo
                                                              WHERE user_id = #{userId}

SELECT content, created
                                                                                     FROM weibo
                                                                                     WHERE id = #{id}
