mapper.update:
UPDATE user

    password = #{password},
    nickname = #{nickname},
    created = #{created}

WHERE id= #{id};

mapper.select:
SELECT COUNT(*)
FROM user
WHERE username = #{username};

SELECT id
FROM user
WHERE username = #{username}
  AND password = #{password};

SELECT COUNT(*)
FROM user
WHERE username = #{username}
  AND password = #{password};
